			typeOfApp: gr.getDisplayValue('x_type_of_application'),
			img:gr.getValue('u_app_image'),
			//likedislike:gr.getDisplayValue('like_dislike')
		}
		console.log(obj);
		 var like_dislike=''
		 var app_user=gs.getUserID();
		var grfav=new GlideRecord('x_ggisu_cdp_app_st_faviorites_app')
				grfav.addQuery('app_name',gr.sys_id);
				grfav.addQuery('app_user',app_user);
				grfav.query();
				if(grfav.next()){
					like_dislike='Like'
				}
		else{
			like_dislike='Dislike'
		}
		obj.like_dislike=like_dislike;

		//get Rating of the app.
	var sysRecord = data.ans;
		console.log('sysrecord'+sysRecord);
	var totalRating=0;
	var countOfRatings=0;

	var grRating = new GlideRecord('x_ggisu_cdp_app_st_app_feedback');
	//grRating.addQuery('sys_id',gr.u_name);
		grRating.addQuery('app_name',gr.sys_id);
grRating.addNotNullQuery('u_rating');
	grRating.query();
	//	console.log(grRating.getDisplayName('app_name'));
		//console.log(gr.u_name)
		while(grRating.next()){


			console.log('sysy'+gr.sys_id);

//	countOfRatings = grRating.getRowCount();
		//countOfRatings = 2;
			countOfRatings = countOfRatings+1;
   console.log('count'+countOfRatings);
		//totalRating = 8 ;
//	while(grRating.next())
	//{
				console.log(grRating.u_rating);
			if(grRating.u_rating!=''){

					totalRating = totalRating + grRating.u_rating;
				  console.log('TotalRating'+totalRating);

			}


	//}

	//obj.ratingAverage = Math.round(totalRating/countOfRatings)
		console.log('coutrating'+countOfRatings);
				console.log('TR'+totalRating);
	data.ratingAverage = Math.round(totalRating/countOfRatings);
			var rating={};
		//	rating.totalRating1=totalRating;
			rating.avgrating1=data.ratingAverage;
			data.allRating.push(rating);
			console.log('RATING ARRAY'+data.allRating);
		console.log('TAVG'+data.ratingAverage);

		}

					var grAvgRating = new GlideRecord('x_ggisu_cdp_app_st_cdp_portal');
					grAvgRating.addQuery('sys_id',gr.sys_id);
					//grAvgRating.addNotNullQuery('u_rating');
					grAvgRating.query();
					if(grAvgRating.next())
						{
							grAvgRating.setValue('u_average_rating',data.ratingAverage);
							grAvgRating.update();
						} 
		//}

	//   }  

		data.appsList.push(obj);
		data.summaryRecords = {
			list: data.appsList,
