sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below

		var ga = new GlideAjax('getCurrentAIURL');
        ga.addParam('sysparm_name', 'getURL');
        ga.getXML(callbackFunction);

        function callbackFunction(response) {
            var answer = response.responseXML.documentElement.getAttribute("answer");
           // alert(answer);

			if(answer=="true")
				{
					g_form.setValue('u_comment_approval','approved');
					g_form.submit();
				}
			if(answer=="false")
				{
					g_form.setValue('u_comment_approval','rejected');
					g_form.submit();
				}
		}
}
