sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {
    //var parser = new JSONParser();
    var details = event.parm2;
    var detailsparsed = JSON.parse(details);
    template.print('<p>Hello ' + detailsparsed.sharewith + '<br/>');
    template.print('<p>' + detailsparsed.sharedfrom +' found this great app on AI App Store and would like to share this app       link with you! Do explore and see how it can be useful for you and your team!' + '<br/>');
    template.print('<p>App Name: ' + detailsparsed.appname + '<br/>');
	if(detailsparsed.application_link){
    template.print('<p>App Link: ' + detailsparsed.application_link + '<br/>');
	}
    template.print('<p>You can also see other apps in the AI App Store ' + '<a href="/aiportal?id=ai_store_home">here</a>');
	if(detailsparsed.comments){
    template.print('<p>Comments from ' + detailsparsed.sharedfrom + ": " + detailsparsed.comments + '</br>');
	}

})(current, template, email, email_action, event);
